import os

def read_file(file_name):
    """
    Read the content of a file.
    
    Args:
        file_name (str): The name of the file to read. It should be the full name to the file, e.g., "/Users/myUser/test.txt".
    
    Returns:
        str: The content of the file, or None if the file does not exist.
    """
    try:
        with open(file_name, 'r') as file:
            content = file.read()
        return content
    except FileNotFoundError:
        return None
    except Exception as e:
        print(f"Error reading the file: {e}")
        return None

def create_file(file_name, content=""):
    """
    Create a new file with the given name and optional content.
    
    Args:
        file_name (str): The name of the file to create. It should be the full name to the file, e.g., "/Users/myUser/test.txt".
        content (str, optional): The initial content of the file. Defaults to an empty string.
    
    Returns:
        bool: True if the file was successfully created, False otherwise.
    """
    try:
        with open(file_name, 'w') as file:
            file.write(content)
        return True
    except Exception as e:
        print(f"Error creating the file: {e}")
        return False

def write_file(file_name, content):
    """
    Write content to an existing file.
    
    Args:
        file_name (str): The name of the file to write to. It should be the full name to the file, e.g., "/Users/myUser/test.txt".
        content (str): The content to write to the file.
    
    Returns:
        bool: True if the content was successfully written to the file, False otherwise.
    """
    try:
        with open(file_name, 'w') as file:
            file.write(content)
        return True
    except Exception as e:
        print(f"Error writing to the file: {e}")
        return False

def delete_file(file_name):
    """
    Delete a file.
    
    Args:
        file_name (str): The name of the file to delete. It should be the full name to the file, e.g., "/Users/myUser/test.txt".
    
    Returns:
        bool: True if the file was successfully deleted, False otherwise.
    """
    try:
        os.remove(file_name)
        return True
    except FileNotFoundError:
        return False
    except Exception as e:
        print(f"Error deleting the file: {e}")
        return False
